mapper.update:
update userinfo u set u.money = #{money},u.nickname
=#{nickname},username=#{username},password=#{password},tel=#{tel},isagent=#{isagent},rebate=#{rebate},owner=#{owner}
where u.id = #{id}

mapper.select:
select
id,openid,nickname,money,createdAt,username,password,tel,isagent,rebate,owner from userinfo
u where u.id = #{id}

select
id,openid,nickname,money,createdAt,username,password,tel,isagent,rebate,owner from userinfo
u where u.tel = #{tel}

		SELECT * FROM userinfo
		WHERE isagent = #{isagent}

			AND owner = #{owner}


          and username = #{username}
